import { useCallback, useState } from 'react';
import Footer from './components/Footer';
import MovieCard from './components/MovieCard';
import Navbar from './components/Navbar';
import SearchBar from './components/SearchBar';
import './styles/App.css';

// --- SIMULAÇÃO DA API ---
// Os dados que você forneceu
const sampleMovieData = {
  "embed_url": "https://vidsrc.xyz/embed/movie?tmdb=9029",
  "id": 9029,
  "title": "Jogo de Amor em Las Vegas",
  "poster_url": "https://image.tmdb.org/t/p/w500/wPfCkAZjxcgbCN7X65Aut8Vtq4c.jpg",
  "description": "Depois da noite mais insana de suas vidas em Las Vegas, dois estranhos sem nada em comum, Jack e Joy, acordam e descobrem que não apenas se divertiram, eles também estão casados! Mas a anulação não vai ser algo tão fácil, pois Jack ganha um prêmio de US$ 3 milhões de dólares apostando com uma moeda de Joy, e um juiz irritado sentencia o casal a seis meses de \"casamento para valer\" antes de dividir o dinheiro.",
  "release_date": "2008-05-07"
};

// Esta função simula uma chamada de API.
// Ela retorna o filme de exemplo se a busca incluir "jogo".
const mockApiSearch = (query) => {
  return new Promise((resolve) => {
    setTimeout(() => { // Simula o atraso da rede
      if (query.toLowerCase().includes('jogo')) {
        resolve([sampleMovieData]); // API de busca geralmente retorna um array
      } else {
        resolve([]); // Retorna array vazio se não encontrar
      }
    }, 1000); // Atraso de 1 segundo
  });
};
// --- FIM DA SIMULAÇÃO ---

function App() {
  const [movie, setMovie] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [searched, setSearched] = useState(false); // Para saber se uma busca já foi feita

  const handleSearch = useCallback(async (query) => {
    setIsLoading(true);
    setError('');
    setSearched(true);
    setMovie(null); // Limpa o resultado anterior

    try {
      // ATENÇÃO: Substitua 'mockApiSearch(query)' pela sua chamada de API real
      const results = await mockApiSearch(query);

      // Uma API real pode retornar múltiplos resultados. Aqui pegamos apenas o primeiro.
      if (results.length > 0) {
        setMovie(results[0]);
      } else {
        setMovie(null); // Nenhum filme encontrado
      }
    } catch (err) {
      setError('Ocorreu um erro ao buscar o filme. Tente novamente.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  }, []);

  return (
    <div className="App">
      <Navbar />
      <main className="main-content">
        <SearchBar onSearch={handleSearch} />
        <div className="results-container">
          {isLoading && <p className="status-message">Carregando...</p>}
          {error && <p className="status-message error">{error}</p>}
          {!isLoading && !error && searched && !movie && (
            <p className="status-message">Nenhum filme encontrado com esse nome.</p>
          )}
          {!isLoading && !error && !searched && (
             <p className="status-message">Comece buscando por um filme no campo acima. Tente "Jogo de Amor em Las Vegas"!</p>
          )}
          {movie && <MovieCard movie={movie} />}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default App;
